using System;using System.Collections.Generic;using System.Text;using RealEstate.DataAccess;namespace RealEstate.Business{	public class CompanyService	{		private static CompanyDAL db = new CompanyDAL();		#region[Company_GetById]		public static List<Company> Company_GetById(string Id)		{			return db.Company_GetById(Id);		}		#endregion		#region[Company_GetByTop]		public static List<Company> Company_GetByTop(string Top, string Where, string Order)		{			return db.Company_GetByTop(Top, Where, Order);		}		#endregion		#region[Company_GetByAll]		public static List<Company> Company_GetByAll()		{			return db.Company_GetByAll();		}		#endregion		#region[Company_Paging]		public static List<Company> Company_Paging(string CurentPage, string PageSize)		{			return db.Company_Paging(CurentPage, PageSize);		}		#endregion		#region[Company_Insert]		public static bool Company_Insert(Company data)		{			return db.Company_Insert(data);		}		#endregion		#region[Company_Update]		public static bool Company_Update(Company data)		{			return db.Company_Update(data);		}		#endregion		#region[Company_Delete]		public static bool Company_Delete(string Id)		{			return db.Company_Delete(Id);		}		#endregion	}}