using System;using System.Collections.Generic;using System.Text;using RealEstate.DataAccess;namespace RealEstate.Business{	public class LinkService	{		private static LinkDAL db = new LinkDAL();		#region[Link_GetById]		public static List<Link> Link_GetById(string Id)		{			return db.Link_GetById(Id);		}		#endregion		#region[Link_GetByTop]		public static List<Link> Link_GetByTop(string Top, string Where, string Order)		{			return db.Link_GetByTop(Top, Where, Order);		}		#endregion		#region[Link_GetByAll]		public static List<Link> Link_GetByAll(string Lang)		{			return db.Link_GetByAll(Lang);		}		#endregion		#region[Link_Paging]		public static List<Link> Link_Paging(string CurentPage, string PageSize, string Lang)		{			return db.Link_Paging(CurentPage, PageSize, Lang);		}		#endregion		#region[Link_Insert]		public static bool Link_Insert(Link data)		{			return db.Link_Insert(data);		}		#endregion		#region[Link_Update]		public static bool Link_Update(Link data)		{			return db.Link_Update(data);		}		#endregion		#region[Link_Delete]		public static bool Link_Delete(string Id)		{			return db.Link_Delete(Id);		}		#endregion	}}