using System;using System.Collections.Generic;using System.Text;using RealEstate.DataAccess;namespace RealEstate.Business{	public class SupportService	{		private static SupportDAL db = new SupportDAL();		#region[Support_GetById]		public static List<Support> Support_GetById(string Id)		{			return db.Support_GetById(Id);		}		#endregion		#region[Support_GetByTop]		public static List<Support> Support_GetByTop(string Top, string Where, string Order)		{			return db.Support_GetByTop(Top, Where, Order);		}		#endregion		#region[Support_GetByAll]		public static List<Support> Support_GetByAll(string Lang)		{			return db.Support_GetByAll(Lang);		}		#endregion		#region[Support_Insert]		public static bool Support_Insert(Support data)		{			return db.Support_Insert(data);		}		#endregion		#region[Support_Update]		public static bool Support_Update(Support data)		{			return db.Support_Update(data);		}		#endregion		#region[Support_Delete]		public static bool Support_Delete(string Id)		{			return db.Support_Delete(Id);		}		#endregion	}}