using System;using System.Data;using System.Data.SqlClient;using System.Collections.Generic;namespace RealEstate.DataAccess{	public class Advertise	{		#region[Declare variables]		private string  _AdvID;
	    private string _RealEstateId;		private string  _Name;		private string  _Image;		private string  _Width;		private string  _Height;		private string  _Link;		private string  _Target;		private string  _Content;		private string  _Position;		private string  _PageId;		private string  _Click;		private string  _Ord;		private string  _Active;		private string  _Lang;		#endregion		#region[Public Properties]
        public string AdvID { get { return _AdvID; } set { _AdvID = value; } }
        public string RealEstateId { get { return _RealEstateId; } set { _RealEstateId = value; } }		public string AdvName{ get { return _Name; } set { _Name = value; } }		public string Image{ get { return _Image; } set { _Image = value; } }		public string Width{ get { return _Width; } set { _Width = value; } }		public string Height{ get { return _Height; } set { _Height = value; } }		public string Link{ get { return _Link; } set { _Link = value; } }		public string Target{ get { return _Target; } set { _Target = value; } }		public string Content{ get { return _Content; } set { _Content = value; } }		public string Position{ get { return _Position; } set { _Position = value; } }		public string PageId{ get { return _PageId; } set { _PageId = value; } }		public string Click{ get { return _Click; } set { _Click = value; } }		public string Ord{ get { return _Ord; } set { _Ord = value; } }		public string Active{ get { return _Active; } set { _Active = value; } }		public string Lang{ get { return _Lang; } set { _Lang = value; } }		#endregion		#region[Advertise IDataReader]		public Advertise AdvertiseIDataReader(IDataReader dr)		{			DataAccess.Advertise obj = new DataAccess.Advertise();
            obj.AdvID = (dr["AdvID"] is DBNull) ? string.Empty : dr["AdvID"].ToString();
            obj.RealEstateId = (dr["RealEstateId"] is DBNull) ? string.Empty : dr["RealEstateId"].ToString();
            obj.AdvName = (dr["AdvName"] is DBNull) ? string.Empty : dr["AdvName"].ToString();			obj.Image = (dr["Image"] is DBNull) ? string.Empty : dr["Image"].ToString();			obj.Width = (dr["Width"] is DBNull) ? string.Empty : dr["Width"].ToString();			obj.Height = (dr["Height"] is DBNull) ? string.Empty : dr["Height"].ToString();			obj.Link = (dr["Link"] is DBNull) ? string.Empty : dr["Link"].ToString();			obj.Target = (dr["Target"] is DBNull) ? string.Empty : dr["Target"].ToString();			obj.Content = (dr["Content"] is DBNull) ? string.Empty : dr["Content"].ToString();			obj.Position = (dr["Position"] is DBNull) ? string.Empty : dr["Position"].ToString();			obj.PageId = (dr["PageId"] is DBNull) ? string.Empty : dr["PageId"].ToString();			obj.Click = (dr["Click"] is DBNull) ? string.Empty : dr["Click"].ToString();			obj.Ord = (dr["Ord"] is DBNull) ? string.Empty : dr["Ord"].ToString();			obj.Active = (dr["Active"] is DBNull) ? string.Empty : dr["Active"].ToString();			obj.Lang = (dr["Lang"] is DBNull) ? string.Empty : dr["Lang"].ToString();			return obj;		}		#endregion	}}