using System;using System.Data;using System.Data.SqlClient;using System.Collections.Generic;namespace RealEstate.DataAccess{	public class Link	{		#region[Declare variables]		private string  _Id;		private string  _Name;		private string  _Line1;		private string  _Line2;		private string  _Link1;		private string  _Link2;		private string  _Position;		private string  _Ord;		private string  _Active;		private string  _Lang;		#endregion		#region[Public Properties]		public string Id{ get { return _Id; } set { _Id = value; } }		public string Name{ get { return _Name; } set { _Name = value; } }		public string Line1{ get { return _Line1; } set { _Line1 = value; } }		public string Line2{ get { return _Line2; } set { _Line2 = value; } }		public string Link1{ get { return _Link1; } set { _Link1 = value; } }		public string Link2{ get { return _Link2; } set { _Link2 = value; } }		public string Position{ get { return _Position; } set { _Position = value; } }		public string Ord{ get { return _Ord; } set { _Ord = value; } }		public string Active{ get { return _Active; } set { _Active = value; } }		public string Lang{ get { return _Lang; } set { _Lang = value; } }		#endregion		#region[Link IDataReader]		public Link LinkIDataReader(IDataReader dr)		{			DataAccess.Link obj = new DataAccess.Link();			obj.Id = (dr["Id"] is DBNull) ? string.Empty : dr["Id"].ToString();			obj.Name = (dr["Name"] is DBNull) ? string.Empty : dr["Name"].ToString();			obj.Line1 = (dr["Line1"] is DBNull) ? string.Empty : dr["Line1"].ToString();			obj.Line2 = (dr["Line2"] is DBNull) ? string.Empty : dr["Line2"].ToString();			obj.Link1 = (dr["Link1"] is DBNull) ? string.Empty : dr["Link1"].ToString();			obj.Link2 = (dr["Link2"] is DBNull) ? string.Empty : dr["Link2"].ToString();			obj.Position = (dr["Position"] is DBNull) ? string.Empty : dr["Position"].ToString();			obj.Ord = (dr["Ord"] is DBNull) ? string.Empty : dr["Ord"].ToString();			obj.Active = (dr["Active"] is DBNull) ? string.Empty : dr["Active"].ToString();			obj.Lang = (dr["Lang"] is DBNull) ? string.Empty : dr["Lang"].ToString();			return obj;		}		#endregion	}}