using System;using System.Data;using System.Data.SqlClient;using System.Collections.Generic;namespace RealEstate.DataAccess{	public class Member	{		#region[Declare variables]		private string  _Id;		private string  _Name;		private string  _Email;		private string  _Username;		private string  _Password;		private string  _GroupMemberId;		private string  _Active;		#endregion		#region[Public Properties]		public string Id{ get { return _Id; } set { _Id = value; } }		public string Name{ get { return _Name; } set { _Name = value; } }		public string Email{ get { return _Email; } set { _Email = value; } }		public string Username{ get { return _Username; } set { _Username = value; } }		public string Password{ get { return _Password; } set { _Password = value; } }		public string GroupMemberId{ get { return _GroupMemberId; } set { _GroupMemberId = value; } }		public string Active{ get { return _Active; } set { _Active = value; } }		#endregion		#region[Member IDataReader]		public Member MemberIDataReader(IDataReader dr)		{			DataAccess.Member obj = new DataAccess.Member();			obj.Id = (dr["Id"] is DBNull) ? string.Empty : dr["Id"].ToString();			obj.Name = (dr["Name"] is DBNull) ? string.Empty : dr["Name"].ToString();			obj.Email = (dr["Email"] is DBNull) ? string.Empty : dr["Email"].ToString();			obj.Username = (dr["Username"] is DBNull) ? string.Empty : dr["Username"].ToString();			obj.Password = (dr["Password"] is DBNull) ? string.Empty : dr["Password"].ToString();			obj.GroupMemberId = (dr["GroupMemberId"] is DBNull) ? string.Empty : dr["GroupMemberId"].ToString();			obj.Active = (dr["Active"] is DBNull) ? string.Empty : dr["Active"].ToString();			return obj;		}		#endregion	}}